mapper.select:
        select
        	*
        from
        jbx_userinfo
        where workspace_id = #{Dto.workspaceId}

            and username = #{Dto.username}


            and employee_number = #{Dto.employeeNumber}


			and mobile = #{Dto.mobile}


			and email = #{Dto.email}


            and user_type = #{Dto.userType}


            and display_name like CONCAT('%', #{Dto.displayName}, '%')

        and deleted = 'n'
        order by sort_index,id

mapper.update:
update jbx_userinfo set
islocked = #{isLocked},
modifieddate = current_timestamp where id = #{id}

update jbx_userinfo set
islocked = #{isLocked}, badpwdcount = 0,
unlockdate = current_timestamp, modifieddate = current_timestamp where id = #{id}

update jbx_userinfo set
password = #{password}, decipherable = #{decipherable}, password_set_type =#{passwordSetType},
password_last_set_time = current_timestamp where id = #{userId}

update jbx_userinfo set
sharedsecret = #{sharedSecret}, sharedcounter = #{sharedCounter},
modifieddate = current_timestamp where id = #{id}
